/** @public */
declare enum LicenseState {
    /**
     * The license key is not defined in app-config.yaml
     */
    NO_KEY_DEFINED = "NO_KEY_DEFINED",
    /**
     * The license key is a temporary trial key.
     */
    TRIAL = "TRIAL",
    /**
     * Temporary trial key has expired.
     */
    TRIAL_EXPIRED = "TRIAL_EXPIRED",
    /**
     * The license key is valid and not a trial key.
     */
    VALID = "VALID",
    /**
     * The license key is defined in app-config.yaml but not valid.
     */
    INVALID = "INVALID",
    /**
     * The license key is valid, but has expired.
     */
    EXPIRED = "EXPIRED",
    /**
     * The license key (valid or trial) has expired past any grace period.
     */
    PAST_GRACE_PERIOD = "PAST_GRACE_PERIOD"
}
/** @public */
type LicenseValidationResponse = {
    state: LicenseState;
    expiry?: string;
    finalExpiry?: string;
    message?: string;
};

export { LicenseState, LicenseValidationResponse };
